import React from 'react'
import './SignIn.css'
import {AiOutlineMail} from 'react-icons/ai'
import {RiLockPasswordLine} from 'react-icons/ri'
import OutsideClickHandler from 'react-outside-click-handler';
import {Shower} from './ForgetPass'
import {CgProfile} from 'react-icons/cg'

export function Close(x){
    document.getElementById(x).style.display='none';
}
export function Show() {
    document.getElementById('signin').style.display='flex';
}
function name() {
    
var email=document.getElementById('email').value;
var x=document.getElementById('x');
var em='',j=0;
for(var i=0;i<email.length;i++){
    if(email[i]==='@'){
        j=i;
    }
}
for(i=j;i<email.length;i++){
em=em+email[i];
}
//alert(em.toString());
if(em==='@diu.edu.bd'){
    x.style.border='1px solid green';
    x.style.color='green';
}else{
    x.style.border='1px solid red';
    x.style.color='red';
}

    
}
function SignUp(){
    document.getElementById('name1').style.display='flex';
    document.getElementById('signup').style.display='initial';
    document.getElementById('signin').style.display='none';
    document.getElementById('sendmail').style.display='none';
    document.getElementById('signup').style.display='initial';
    document.getElementById('flat').style.display='none';

}
document.getElementById('forget-pass').addEventListener('click',function(){
    document.getElementById('name1').style.display='none';
    document.getElementById('password1').style.display='none';
    document.getElementById('signup').style.display='none';
    document.getElementById('signin').style.display='none';
    document.getElementById('sendmail').style.display='initial';
    document.getElementById('signup').style.display='none';
    document.getElementById('flat').style.display='none';
});
function handleEnter(event) {
    if (event.keyCode === 13) {
      const form = event.target.form;
      const index = Array.prototype.indexOf.call(form, event.target);
      form.elements[index + 1].focus();
      event.preventDefault();
    }
  }

function SignIn (props){

    return(
        <div className='signin' style={{display: props.display}} id='signin'>
            <OutsideClickHandler onOutsideClick={()=>{
                Close('signin');
            }}>
                <div className='bx1'>
                <div className='bx2'>
                    <h4 id='head'>Sign In Here</h4>
                </div>
                <div className='bx' id='name1'>
                    <CgProfile></CgProfile>
                    <input type='text' placeholder='Name.....'  id='name' onChange={name} onKeyDown={handleEnter}></input>
                </div>
                <div className='bx' id='email1'>
                    <AiOutlineMail></AiOutlineMail>
                    <input type='text' placeholder='Email.....'  id='email' onChange={name} onKeyDown={handleEnter}></input>
                </div>
                <div className='bx' id='password1'>
                    <RiLockPasswordLine></RiLockPasswordLine>
                    <input type='password' placeholder='Password.....' id='password'></input>
                </div>
                <div className='bx2'>
                    <input type='submit' value='Sign In' id='signin'></input>
                    <input type='submit' value='Sign Up' id='signup'></input>
                    <input type='submit' value='Send Mail' id='sendmail'></input>
                </div>
                <div className='bx2' id='flat'>
                    <input type='submit' value='Create Account' onClick={SignUp}></input>
                    <input type='submit' value='Forget Password' id='forget-pass'></input>
                </div>
            </div>
            </OutsideClickHandler>
        </div>
    ); 
}
export default SignIn
