import React from 'react'
import './SignIn.css'
import {AiOutlineMail} from 'react-icons/ai'
import OutsideClickHandler from 'react-outside-click-handler';
import {Close} from './SignIn'


export function Shower() {

    document.getElementById('forget').style.display='flex';
}
function Closer(action){
    document.getElementById('forget').style.display=action;
}
function ForgetPass(params) {
    return(
        <div className='signin' id='forget'>
        <OutsideClickHandler onOutsideClick={()=>{
                Closer("none");
            }}>
                <div className='bx1'>
                <div className='bx2'>
                    <h4>Sign In Here</h4>
                </div>
                <div className='bx'>
                    <AiOutlineMail></AiOutlineMail>
                    <input type='text' placeholder='Email.....'></input>
                </div>
                <div className='bx2'>
                    <input type='submit' value='Send Mail'></input>
                </div>
            </div>
            </OutsideClickHandler>
        </div>
    )
}
export default ForgetPass;
