import React from 'react'
import './SignIn.css'
import {AiOutlineMail} from 'react-icons/ai'
import {RiLockPasswordLine} from 'react-icons/ri'
import OutsideClickHandler from 'react-outside-click-handler';
import {Shower} from './ForgetPass'
import { Color } from '../../App';

export function Close(){
    document.getElementById('signin').style.display='none';
}
export function Show() {
    document.getElementById('signin').style.display='flex';
}
var border,color;
function name() {
    alert('')
var email=document.getElementById('email').value;
var em=[],j=0;
for(var i=0;i<String.length;i++){
    if(email[i]==='@'){
        em[j]=email[i];
    }
}
if(em==='@diu.edu.bd'){
    border='1px solid green';
    color='green';
}else{
    border='1px solid red';
    color='red';
}

    
}
function SignIn (props){

    return(
        <div className='signin' style={{display: props.display}} id='signin'>
            <OutsideClickHandler onOutsideClick={()=>{
                Close();
            }}>
                <div className='bx1'>
                <div className='bx2'>
                    <h4>Sign In Here</h4>
                </div>
                <div className='bx' style={{border: border, color: color}}>
                    <AiOutlineMail></AiOutlineMail>
                    <input type='text' placeholder='Email.....' onTouchStart={name} id='email'></input>
                </div>
                <div className='bx'>
                    <RiLockPasswordLine></RiLockPasswordLine>
                    <input type='password' placeholder='Password.....'></input>
                </div>
                <div className='bx2'>
                    <input type='submit' value='Sign In' onClick={name}></input>
                </div>
                <div className='bx2'>
                    <input type='submit' value='Create Account'></input>
                    <input type='submit' value='Forget Password' onClick={Shower}></input>
                </div>
            </div>
            </OutsideClickHandler>
        </div>
    ); 
}
export default SignIn
